import React, { useState } from "react";
import "./App.css";
import "./Style.css";
import { Scene } from "./canvas/Scene";
import ColorsSlider from "./components/ColorsSlider";
import { OptionsMenu } from "./components/OptionsMenu";
import { COLORS } from "./constants/colors";
